Template.TEMPLATE_NAME.helpers({
	"geigerChart": function() {
		var cols = [];
		var col = ["Radiation"];
		var data = _.pluck(Sensors.find({ topic: "revspace/sensors/geiger" }).fetch(), "message");
		_.each(data, function(g) {
			col.push(parseInt(g));
		});
		cols.push(col);
		return {
			data: {
				columns: cols,
				type: 'spline'
			}
		};
	}
});
